package swap_pack;

public class swap_class {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x=10,y=20,z;
System.out.println("values of x and y before swapping");
System.out.println("x=");System.out.print(x);
System.out.println("\ny=");System.out.print(y);
z=x;
x=y;
y=z;
System.out.println("\nvalues of x and y after swapping");
System.out.println("x=");System.out.print(x);
System.out.println("\ny=");System.out.print(y);
	}

}
